import React, { useState } from "react";
import { startOfMonth, endOfMonth, startOfWeek, endOfWeek, addDays, format, isSameMonth, isSameDay, subMonths, addMonths } from "date-fns";
import { ja } from "date-fns/locale";

export default function SmallCalendar({ selected, onSelect }) {
  const [monthBase, setMonthBase] = useState(selected || new Date());

  const start = startOfWeek(startOfMonth(monthBase), { weekStartsOn: 1 });
  const end = endOfWeek(endOfMonth(monthBase), { weekStartsOn: 1 });

  const days = [];
  for (let d = start; d <= end; d = addDays(d, 1)) {
    days.push(d);
  }

  return (
    <div className="small-calendar card">
      <div className="card-body p-2">
        <div className="d-flex justify-content-between align-items-center mb-2">
          <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setMonthBase((m) => subMonths(m, 1))}>◀</button>
          <div className="fw-bold">{format(monthBase, "yyyy年M月", { locale: ja })}</div>
          <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setMonthBase((m) => addMonths(m, 1))}>▶</button>
        </div>

        <div className="row gx-1 mb-2 text-center small" style={{fontWeight:600}}>
          {["月","火","水","木","金","土","日"].map((d, i) => (
            <div key={i} className="col">{d}</div>
          ))}
        </div>

        <div className="row g-1">
          {days.map((d, i) => {
            const inMonth = isSameMonth(d, monthBase);
            const isSelected = selected && isSameDay(d, selected);
            return (
              <div key={i} className="col-1-7"> {/* custom width handled by CSS below fallback */}
                <button
                  className={`btn btn-sm w-100 ${isSelected ? "btn-info text-white" : "btn-light"}`}
                  onClick={() => onSelect && onSelect(d)}
                  title={format(d, "yyyy-MM-dd", { locale: ja })}
                  style={{ padding: "6px 4px", fontSize: 13, borderRadius: 6 }}
                >
                  {format(d, "d")}
                </button>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
