import React, { useState } from "react";
import Gantt from "./components/Gantt";
import "./App.css";

export default function App() {
    const [view, setView] = useState("month"); // "month" | "week" | "day"
    const [currentDate, setCurrentDate] = useState(new Date());

    const sampleTasks = [
        { id: 1, title: "企画会議", start: "2025-09-02", end: "2025-09-04", color: "#4caf50" },
        { id: 2, title: "実装A", start: "2025-09-03", end: "2025-09-10", color: "#2196f3" },
        { id: 3, title: "テスト", start: "2025-09-09", end: "2025-09-12", color: "#ff9800" },
        { id: 4, title: "デプロイ", start: "2025-09-15", end: "2025-09-15", color: "#e91e63" },
    ];

    return (
        <div className="app">
            <header className="toolbar">
                <div>
                    <button onClick={() => setView("month")} className={view==="month"?"active":""}>月</button>
                    <button onClick={() => setView("week")} className={view==="week"?"active":""}>週</button>
                    <button onClick={() => setView("day")} className={view==="day"?"active":""}>日</button>
                </div>
                <div className="nav">
                    <button onClick={() => setCurrentDate(d => new Date(d.getFullYear(), d.getMonth(), d.getDate()-1))}>前</button>
                    <span className="current">{currentDate.toDateString()}</span>
                    <button onClick={() => setCurrentDate(d => new Date(d.getFullYear(), d.getMonth(), d.getDate()+1))}>次</button>
                </div>
            </header>

            <main>
                <Gantt tasks={sampleTasks} view={view} currentDate={currentDate} />
            </main>
        </div>
    );
}
