import React, { useState, useEffect } from "react";
import { addMonths, addWeeks, addDays } from "date-fns";
import Gantt from "./components/Gantt";
import "./App.css";

export default function App() {
    const [view, setView] = useState("month"); // "month" | "week" | "day"
    const [currentDate, setCurrentDate] = useState(new Date());

    const [tasks, setTasks] = useState([
        { id: 1, title: "企画会議", start: "2025-09-02", end: "2025-09-04", color: "#4caf50", description: "要件確認" },
        { id: 2, title: "実装A", start: "2025-09-03", end: "2025-09-10", color: "#2196f3", description: "主要機能" },
        { id: 3, title: "テスト", start: "2025-09-09", end: "2025-09-12", color: "#ff9800", description: "結合テスト" },
        { id: 4, title: "デプロイ", start: "2025-09-15", end: "2025-09-15", color: "#e91e63", description: "本番反映" },
    ]);

    const [editingTask, setEditingTask] = useState(null);

    // view navigation
    const goPrev = () => {
        setCurrentDate(d => {
            if (view === "month") return addMonths(d, -1);
            if (view === "week") return addWeeks(d, -1);
            return addDays(d, -1);
        });
    };
    const goNext = () => {
        setCurrentDate(d => {
            if (view === "month") return addMonths(d, 1);
            if (view === "week") return addWeeks(d, 1);
            return addDays(d, 1);
        });
    };

    return (
        <div className="app">
            <header className="toolbar">
                <div>
                    <button onClick={() => setView("month")} className={view==="month"?"active":""}>月</button>
                    <button onClick={() => setView("week")} className={view==="week"?"active":""}>週</button>
                    <button onClick={() => setView("day")} className={view==="day"?"active":""}>日</button>
                </div>
                <div className="nav">
                    <button onClick={goPrev}>前</button>
                    <span className="current">{currentDate.toDateString()}</span>
                    <button onClick={goNext}>次</button>
                </div>
            </header>

            <main>
                <Gantt
                  tasks={tasks}
                  view={view}
                  currentDate={currentDate}
                  onTaskClick={(t)=>setEditingTask(t)}
                  editingTaskId={editingTask?.id}
                />
            </main>
        </div>
    );
}
