import React, { useState, useEffect } from "react";
import { addMonths, addWeeks, addDays, format } from "date-fns";
import { ja } from "date-fns/locale";
import Gantt from "./components/Gantt";
import SmallCalendar from "./components/SmallCalendar";
import "./App.css";

export default function App() {
    const [view, setView] = useState("month"); // "month" | "week" | "day"
    const [currentDate, setCurrentDate] = useState(new Date());

    const [tasks, setTasks] = useState([
        { id: 1, title: "‰ºÅÁîª‰ºöË≠∞", start: "2025-09-02", end: "2025-09-04", color: "#4caf50", description: "Ë¶Å‰ª∂Á¢∫Ë™ç", members: ["‰ΩêËó§","Èà¥Êú®"] },
        { id: 2, title: "ÂÆüË£ÖA", start: "2025-09-03", end: "2025-09-10", color: "#2196f3", description: "‰∏ªË¶ÅÊ©üËÉΩ", members: ["Áî∞‰∏≠"] },
        { id: 3, title: "„ÉÜ„Çπ„Éà", start: "2025-09-09", end: "2025-09-12", color: "#ff9800", description: "ÁµêÂêà„ÉÜ„Çπ„Éà", members: ["È´òÊ©ã","ÊñéËó§"] },
        { id: 4, title: "„Éá„Éó„É≠„Ç§", start: "2025-09-15", end: "2025-09-15", color: "#e91e63", description: "Êú¨Áï™ÂèçÊò†", members: ["Â±±Áî∞"] },
    ]);

    // Á∑®ÈõÜ‰∏≠„ÅÆ„Çø„Çπ„ÇØÔºànull: ËøΩÂä†„É¢„Éº„ÉâÔºâ
    const [editingTask, setEditingTask] = useState(null);

    // „Éï„Ç©„Éº„É†Áî® state
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [membersInput, setMembersInput] = useState(""); // „Ç´„É≥„ÉûÂå∫Âàá„Çä„ÅÆÂÖ•Âäõ
    const [startDate, setStartDate] = useState(() => {
        const d = new Date();
        return d.toISOString().slice(0,10);
    });
    const [startTime, setStartTime] = useState("09:00");
    const [endDate, setEndDate] = useState(() => {
        const d = new Date();
        return d.toISOString().slice(0,10);
    });
    const [endTime, setEndTime] = useState("17:00");
    const [color, setColor] = useState("#4caf50");

    // editingTask „ÅåÂ§â„Çè„Å£„Åü„Çâ„Éï„Ç©„Éº„É†„Çí„Éó„É™„Çª„ÉÉ„Éà„ÄÅnull„Å™„ÇâÂàùÊúüÂÄ§„Å´Êàª„Åô
    useEffect(() => {
        if (editingTask) {
            const s = editingTask.start || "";
            const e = editingTask.end || "";
            const [sDate, sTime] = s.split("T");
            const [eDate, eTime] = e.split("T");
            setTitle(editingTask.title || "");
            setDescription(editingTask.description || "");
            setMembersInput((editingTask.members || []).join(", "));
            setStartDate(sDate || new Date().toISOString().slice(0,10));
            setStartTime(sTime || "09:00");
            setEndDate(eDate || new Date().toISOString().slice(0,10));
            setEndTime(eTime || "17:00");
            setColor(editingTask.color || "#4caf50");
        } else {
            const today = new Date().toISOString().slice(0,10);
            setTitle("");
            setDescription("");
            setMembersInput("");
            setStartDate(today);
            setStartTime("09:00");
            setEndDate(today);
            setEndTime("17:00");
            setColor("#4caf50");
        }
    }, [editingTask]);

    const addTask = (e) => {
        e.preventDefault();
        if (!title.trim()) return window.alert("„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");

        const sTime = startTime || "00:00";
        const eTime = endTime || "00:00";
        const startISO = `${startDate}T${sTime}`;
        const endISO = `${endDate}T${eTime}`;

        const members = membersInput
          .split(",")
          .map(m => m.trim())
          .filter(Boolean);

        const newTask = {
            id: Date.now(),
            title: title.trim(),
            description: description.trim(),
            start: startISO,
            end: endISO,
            color,
            members,
        };

        setTasks(prev => [...prev, newTask]);

        // „É™„Çª„ÉÉ„Éà
        setTitle("");
        setDescription("");
        setMembersInput("");
    };

    const updateTask = (e) => {
        e.preventDefault();
        if (!editingTask) return;
        if (!title.trim()) return window.alert("„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");

        const sTime = startTime || "00:00";
        const eTime = endTime || "00:00";
        const startISO = `${startDate}T${sTime}`;
        const endISO = `${endDate}T${eTime}`;

        const members = membersInput
          .split(",")
          .map(m => m.trim())
          .filter(Boolean);

        const updated = {
            ...editingTask,
            title: title.trim(),
            description: description.trim(),
            start: startISO,
            end: endISO,
            color,
            members,
        };

        setTasks(prev => prev.map(t => t.id === editingTask.id ? updated : t));
        setEditingTask(null);
    };

    const deleteTask = () => {
        if (!editingTask) return;
        if (!window.confirm("Êú¨ÂΩì„Å´ÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü")) return;
        setTasks(prev => prev.filter(t => t.id !== editingTask.id));
        setEditingTask(null);
    };

    const cancelEdit = () => {
        setEditingTask(null);
    };

    // view „Å´Âøú„Åò„Å¶ÂâçÔºèÊ¨°ÁßªÂãï
    const goPrev = () => {
        setCurrentDate(d => {
            if (view === "month") return addMonths(d, -1);
            if (view === "week") return addWeeks(d, -1);
            return addDays(d, -1);
        });
    };
    const goNext = () => {
        setCurrentDate(d => {
            if (view === "month") return addMonths(d, 1);
            if (view === "week") return addWeeks(d, 1);
            return addDays(d, 1);
        });
    };

    // task update from Gantt (timing changed)
    const handleTaskUpdate = (updated) => {
        setTasks(prev => prev.map(t => t.id === updated.id ? { ...t, ...updated } : t));
        // clear editing if updated while editing
        setEditingTask((et) => (et && et.id === updated.id ? { ...et, ...updated } : et));
    };

    // „Çµ„Ç§„Éâ„Éê„ÉºÊäò„Çä„Åü„Åü„ÅøÁä∂ÊÖã
    const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

    // layout: sidebar + main content
    return (
        <div className="app">
            <div className={`layout ${sidebarCollapsed ? "collapsed" : ""}`}>
                <aside className="sidebar">
                    {/* Êäò„Çä„Åü„Åü„Åø„Éú„Çø„É≥ÔºàÂ∏∏„Å´Ë°®Á§∫Ôºâ */}
                    <div className="sidebar-toggle">
                      <button onClick={() => setSidebarCollapsed(s => !s)} aria-label={sidebarCollapsed ? "Â±ïÈñã" : "Êäò„Çä„Åü„Åü„ÇÄ"}>
                        {sidebarCollapsed ? "‚ñ∂" : "‚óÄ"}
                      </button>
                    </div>

                    <div className="sidebar-section">
                      <div style={{fontWeight:700, marginBottom:6}}>Ë°®Á§∫ÂàáÊõø</div>

                      {/* „Éï„É´Ë°®Á§∫„ÅÆ„Éú„Çø„É≥Áæ§ */}
                      <div className="view-buttons full-view" style={{display:"flex", flexDirection:"column", gap:6}}>
                        <button onClick={() => setView("month")} className={view==="month"?"active":""}>Êúà</button>
                        <button onClick={() => setView("week")} className={view==="week"?"active":""}>ÈÄ±</button>
                        <button onClick={() => setView("day")} className={view==="day"?"active":""}>Êó•</button>
                      </div>

                      {/* Êäò„Çä„Åü„Åü„ÅøÊôÇ„Å´Ë°®Á§∫„Åô„Çã„Ç≥„É≥„Éë„ÇØ„Éà„Å™„Ç¢„Ç§„Ç≥„É≥Ôºà„ÉÜ„Ç≠„Çπ„Éà„ÇíÈö†„ÅôÔºâ */}
                      <div className="view-buttons compact-view" style={{display:"none", flexDirection:"column", gap:6, alignItems:"center"}}>
                        <button className={view==="month"?"active":""} onClick={()=>setView("month")} title="Êúà">üóì</button>
                        <button className={view==="week"?"active":""} onClick={()=>setView("week")} title="ÈÄ±">üìÖ</button>
                        <button className={view==="day"?"active":""} onClick={()=>setView("day")} title="Êó•">üïí</button>
                      </div>
                    </div>

                    <div className="sidebar-section">
                      {/* „Éï„É´„Éä„ÉìÔºàÈùûÊäò„Çä„Åü„Åü„ÅøÊôÇÔºâ */}
                      <div className="full-nav" style={{display:"block"}}>
                        <div style={{display:"flex", gap:8, alignItems:"center", marginBottom:8}}>
                          <button onClick={goPrev}>‚óÄ</button>
                          <div style={{fontWeight:600}} className="nav-date">{format(currentDate, "yyyyÂπ¥MÊúàdÊó• (EEE)", { locale: ja })}</div>
                          <button onClick={goNext}>‚ñ∂</button>
                        </div>
                        <button onClick={() => setCurrentDate(new Date())}>‰ªäÊó•</button>
                      </div>

                      {/* Êäò„Çä„Åü„Åü„ÅøÊôÇ„ÅÆÁ∞°Êòì„Éä„Éì */}
                      <div className="compact-nav" style={{display:"none", textAlign:"center"}}>
                        <div style={{display:"flex", flexDirection:"column", gap:6}}>
                          <button onClick={goPrev} title="Ââç">‚óÄ</button>
                          <button onClick={() => setCurrentDate(new Date())} title="‰ªäÊó•">‰ªäÊó•</button>
                          <button onClick={goNext} title="Ê¨°">‚ñ∂</button>
                        </div>
                      </div>
                    </div>

                    <div className="sidebar-section small-calendar-section">
                      <SmallCalendar selected={currentDate} onSelect={(d)=>setCurrentDate(d)} />
                    </div>
                </aside>

                <main className="content">
                    {/* ‰∫àÂÆöËøΩÂä†ÔºèÁ∑®ÈõÜ„Éï„Ç©„Éº„É† */}
                    <section className="add-form" style={{ marginBottom: 12, padding: 10, border: "1px solid #eee", borderRadius: 6 }}>
                        <form onSubmit={editingTask ? updateTask : addTask} style={{display:"grid", gridTemplateColumns:"repeat(6, 1fr)", gap:8, alignItems:"center"}}>
                            <input type="text" placeholder="„Çø„Ç§„Éà„É´" value={title} onChange={e=>setTitle(e.target.value)} style={{gridColumn:"span 2", padding:6}} />
                            <input type="text" placeholder="„É°„É≥„Éê„Éº („Ç´„É≥„ÉûÂå∫Âàá„Çä)" value={membersInput} onChange={e=>setMembersInput(e.target.value)} style={{padding:6}} />
                            <input type="date" value={startDate} onChange={e=>setStartDate(e.target.value)} style={{padding:6}} />
                            <input type="time" value={startTime} onChange={e=>setStartTime(e.target.value)} style={{padding:6}} />
                            <input type="date" value={endDate} onChange={e=>setEndDate(e.target.value)} style={{padding:6}} />
                            <input type="time" value={endTime} onChange={e=>setEndTime(e.target.value)} style={{padding:6}} />

                            <textarea placeholder="Ë™¨Êòé" value={description} onChange={e=>setDescription(e.target.value)} style={{gridColumn:"1 / span 4", padding:6, minHeight:40}} />

                            <input type="color" value={color} onChange={e=>setColor(e.target.value)} title="Ëâ≤" style={{width:44, height:36, padding:0, border:"none"}} />

                            <div style={{display:"flex", gap:8, alignItems:"center"}}>
                                <button type="submit" className={editingTask ? "btn-update" : "btn-add"} style={{padding:"8px 12px"}}>{editingTask ? "Êõ¥Êñ∞" : "ËøΩÂä†"}</button>
                                {editingTask && <button type="button" onClick={deleteTask} style={{padding:"8px 12px", background:"#e53935", color:"white"}}>ÂâäÈô§</button>}
                                {editingTask && <button type="button" onClick={cancelEdit} style={{padding:"8px 12px"}}>„Ç≠„É£„É≥„Çª„É´</button>}
                            </div>
                        </form>
                    </section>

                    {/* ÂÖÉ„ÅÆÂçò‰∏Ä„Ç∞„É™„ÉÉ„ÉâÁä∂ÊÖã„Å´Êàª„ÅôÔºà.gantt-wrap „Çí‰Ωø„Çè„Å™„ÅÑÔºâ */}
                    <div style={{ paddingTop: 6 }}>
                        <Gantt
                          tasks={tasks}
                          view={view}
                          currentDate={currentDate}
                          onTaskClick={(t)=>setEditingTask(t)}
                          editingTaskId={editingTask?.id}
                          onTaskUpdate={handleTaskUpdate}
                        />
                    </div>
                </main>
            </div>
        </div>
    );
}
