import React, { useRef, useState, useEffect } from "react";
import {
  parseISO,
  startOfMonth,
  endOfMonth,
  startOfWeek,
  endOfWeek,
  startOfDay,
  endOfDay,
  differenceInDays,
  differenceInHours,
  addDays,
  addHours,
  format,
} from "date-fns";
import { ja } from "date-fns/locale";

function getRange(view, currentDate) {
  if (view === "month") {
    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    const days = differenceInDays(end, start) + 1;
    const columns = Array.from({ length: days }).map((_, i) => addDays(start, i));
    return { start, end, columns, unit: "day" };
  }
  if (view === "week") {
    const start = startOfWeek(currentDate, { weekStartsOn: 1 });
    const end = endOfWeek(currentDate, { weekStartsOn: 1 });
    const days = differenceInDays(end, start) + 1;
    const columns = Array.from({ length: days }).map((_, i) => addDays(start, i));
    return { start, end, columns, unit: "day" };
  }
  // day view: use 24 hours
  const start = startOfDay(currentDate);
  const end = endOfDay(currentDate);
  const columns = Array.from({ length: 24 }).map((_, i) => addHours(start, i));
  return { start, end, columns, unit: "hour" };
}

export default function Gantt({ tasks, view, currentDate, onTaskClick, editingTaskId }) {
  const range = getRange(view, currentDate);

  const containerRef = useRef(null);
  const [containerWidth, setContainerWidth] = useState(800);

  useEffect(() => {
    const measure = () => {
      const w = containerRef.current ? containerRef.current.clientWidth : 800;
      setContainerWidth(Math.max(200, w));
    };
    measure();
    window.addEventListener("resize", measure);
    return () => window.removeEventListener("resize", measure);
  }, []);

  // 列幅のレスポンシブ計算（右側チャート部分の列幅）
  const leftColumnWidth = 220; // 左タイトル列の固定幅（必要なら調整）
  const availableForRight = Math.max(200, containerWidth - leftColumnWidth);
  const minColWidth = range.unit === "hour" ? 80 : 60; // 最小幅
  const desired = availableForRight / Math.max(1, range.columns.length);
  const colWidth = desired < minColWidth ? minColWidth : desired;
  const innerWidthRight = Math.ceil(colWidth * range.columns.length);

  const totalUnits =
    range.unit === "day"
      ? differenceInDays(range.end, range.start) + 1
      : differenceInHours(range.end, range.start) + 1;

  const unitLabel = (d) =>
    range.unit === "day" ? format(d, "MM/dd (EEE)", { locale: ja }) : format(d, "HH:00");

  const toPercent = (date) => {
    const dt = typeof date === "string" ? parseISO(date) : date;
    const diff =
      range.unit === "day" ? differenceInDays(dt, range.start) : differenceInHours(dt, range.start);
    return (diff / totalUnits) * 100;
  };

  // 表示範囲と重なるタスクだけにフィルタする（完全に範囲外の予定を除外）
  const visibleTasks = tasks.filter((t) => {
    try {
      const s = parseISO(t.start);
      const e = parseISO(t.end);
      return e >= range.start && s <= range.end;
    } catch {
      return false;
    }
  });

  return (
    <div className="gantt-with-left" ref={containerRef}>
      {/* 左側タイトル列 */}
      <div className="gantt-left" style={{ width: leftColumnWidth }}>
        <div className="left-header">予定</div>
        <div className="left-list">
          {visibleTasks.map((t) => {
            const start = parseISO(t.start);
            const end = parseISO(t.end);
            const dateLabel =
              range.unit === "hour"
                ? `${format(start, "MM/dd HH:mm", { locale: ja })}`
                : `${format(start, "MM/dd", { locale: ja })}`;

            const isSelected = editingTaskId && editingTaskId === t.id;
            return (
              <div
                key={t.id}
                className={`left-row${isSelected ? " selected" : ""}`}
                style={{ height: 56, display: "flex", flexDirection: "column", justifyContent: "center", padding: "8px 12px", cursor: "pointer" }}
                onClick={() => onTaskClick && onTaskClick(t)}
                title={t.description || ""}
              >
                <div className="left-title" style={{ fontWeight: 700, whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>{t.title}</div>
                <div className="left-date" style={{ fontSize: 12, opacity: 0.9, marginTop: 4 }}>{dateLabel}</div>
              </div>
            );
          })}
        </div>
      </div>

      {/* 右側チャート（横スクロール） */}
      <div className="gantt" style={{ overflowX: "auto", flex: 1 }}>
        <div className="gantt-inner" style={{ width: innerWidthRight, minWidth: innerWidthRight, boxSizing: "border-box" }}>
          <div className="timeline" style={{ display: "flex" }}>
            {range.columns.map((col, i) => {
              const day = col.getDay();
              const isSat = day === 6;
              const isSun = day === 0;
              const labelStyle = { color: isSat ? "#1976d2" : isSun ? "#e53935" : "inherit" };
              return (
                <div key={i} className="time-col" style={{ width: colWidth, boxSizing: "border-box", textAlign: "center", borderRight: "1px solid #f0f0f0", padding: 8 }}>
                  <div className="time-label" style={labelStyle}>{unitLabel(col)}</div>
                </div>
              );
            })}
          </div>

          <div className="track-area" style={{ position: "relative", padding: 10 }}>
            {visibleTasks.map((t) => {
              const start = parseISO(t.start);
              const end = parseISO(t.end);
              const startClamped = start < range.start ? range.start : start;
              const endClamped = end > range.end ? range.end : end;
              const leftPct = toPercent(startClamped);
              const rightPct = toPercent(endClamped);
              const widthPct = Math.max(0.5, rightPct - leftPct);

              // convert percent -> px relative to right inner width
              const leftPx = (leftPct / 100) * innerWidthRight;
              const widthPx = (widthPct / 100) * innerWidthRight;

              const isSelected = editingTaskId && editingTaskId === t.id;

              return (
                <div className="track-row" key={t.id} style={{ height: 56, position: "relative", marginBottom: 8 }}>
                  <div
                    className={`task${isSelected ? " selected" : ""}`}
                    style={{
                      left: leftPx,
                      width: widthPx,
                      background: t.color,
                      position: "absolute",
                      height: 52,
                      borderRadius: 6,
                      color: "white",
                      padding: 0,
                      boxSizing: "border-box",
                      overflow: "hidden",
                      cursor: "pointer",
                    }}
                    // 棒自体は編集しない。編集は左列で行うがクリックしても選択可能にしておく
                    onClick={() => onTaskClick && onTaskClick(t)}
                    title={`${t.title}\n${t.description || ""}\n${format(start, "yyyy-MM-dd HH:mm", { locale: ja })} - ${format(end, "yyyy-MM-dd HH:mm", { locale: ja })}`}
                  >
                    {/* 棒の中は空にしておく（タイトル等は左列で表示） */}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
