import React, { useRef, useState, useEffect } from "react";
import {
  parseISO,
  startOfMonth,
  endOfMonth,
  startOfWeek,
  endOfWeek,
  startOfDay,
  endOfDay,
  differenceInDays,
  differenceInHours,
  addDays,
  addHours,
  format,
} from "date-fns";
import { ja } from "date-fns/locale";

function getRange(view, currentDate) {
  if (view === "month") {
    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    const days = differenceInDays(end, start) + 1;
    const columns = Array.from({ length: days }).map((_, i) => addDays(start, i));
    return { start, end, columns, unit: "day" };
  }
  if (view === "week") {
    const start = startOfWeek(currentDate, { weekStartsOn: 1 });
    const end = endOfWeek(currentDate, { weekStartsOn: 1 });
    const days = differenceInDays(end, start) + 1;
    const columns = Array.from({ length: days }).map((_, i) => addDays(start, i));
    return { start, end, columns, unit: "day" };
  }
  const start = startOfDay(currentDate);
  const end = endOfDay(currentDate);
  const columns = Array.from({ length: 24 }).map((_, i) => addHours(start, i));
  return { start, end, columns, unit: "hour" };
}

export default function Gantt({ tasks, view, currentDate, onTaskClick, editingTaskId }) {
  const range = getRange(view, currentDate);

  const containerRef = useRef(null);
  const [containerWidth, setContainerWidth] = useState(() => (typeof window !== "undefined" ? window.innerWidth : 900));

  useEffect(() => {
    const measure = () => {
      const el = containerRef.current;
      const w = el ? el.clientWidth : (typeof window !== "undefined" ? window.innerWidth : 900);
      setContainerWidth(Math.max(300, w));
    };
    measure();
    window.addEventListener("resize", measure);
    return () => window.removeEventListener("resize", measure);
  }, []);

  // layout
  const leftColumnWidth = 220;
  const availableForRight = Math.max(200, containerWidth - leftColumnWidth);
  const minColWidth = range.unit === "hour" ? 60 : 50;
  const desired = availableForRight / Math.max(1, range.columns.length);
  const colWidth = desired < minColWidth ? minColWidth : desired;
  const cols = range.columns.length;
  const gridTemplate = `${leftColumnWidth}px repeat(${cols}, ${colWidth}px)`;

  // visible tasks within range
  const rStartMs = range.start.getTime();
  const rEndMs = range.end.getTime();
  const visibleTasks = tasks.filter((t) => {
    try {
      const sMs = parseISO(t.start).getTime();
      const eMs = parseISO(t.end).getTime();
      return eMs >= rStartMs && sMs <= rEndMs;
    } catch {
      return false;
    }
  });

  const formatHeader = (d) => (range.unit === "day" ? format(d, "MM/dd (EEE)", { locale: ja }) : format(d, "HH:00"));

  // helpers to compute column index (0-based)
  const colIndexOf = (dt) => {
    const d = typeof dt === "string" ? parseISO(dt) : dt;
    if (range.unit === "day") {
      return Math.max(0, Math.min(cols - 1, differenceInDays(d, range.start)));
    }
    return Math.max(0, Math.min(cols - 1, differenceInHours(d, range.start)));
  };

  return (
    <div className="gantt-with-left" ref={containerRef}>
      {/* header row as grid */}
      <div className="grid header" style={{ display: "grid", gridTemplateColumns: gridTemplate, alignItems: "stretch", marginBottom: 6 }}>
        <div className="grid-left-header" style={{ padding: 10, boxSizing: "border-box", background: "#fff", border: "1px solid #eee", borderRight: "none", fontWeight: 700 }}>
          予定
        </div>
        {range.columns.map((col, i) => {
          const day = col.getDay();
          const isSat = day === 6;
          const isSun = day === 0;
          const labelStyle = { color: isSat ? "#1976d2" : isSun ? "#e53935" : "inherit", padding: 8, boxSizing: "border-box", textAlign: "center", borderLeft: "1px solid #f0f0f0", borderBottom: "1px solid #eee" };
          return (
            <div key={i} className="grid-header-cell" style={labelStyle}>
              {formatHeader(col)}
            </div>
          );
        })}
      </div>

      {/* rows: each row is a grid with left title cell + day/hour cells */}
      <div className="grid-rows" style={{ maxHeight: 520, overflow: "auto" }}>
        {visibleTasks.map((t) => {
          const start = parseISO(t.start);
          const end = parseISO(t.end);
          const startClamped = start < range.start ? range.start : start;
          const endClamped = end > range.end ? range.end : end;
          const startIdx = colIndexOf(startClamped);
          const endIdx = colIndexOf(endClamped);
          const span = Math.max(1, endIdx - startIdx + 1);

          const isSelected = editingTaskId && editingTaskId === t.id;

          return (
            <div key={t.id} className={`grid row ${isSelected ? "selected" : ""}`} style={{ display: "grid", gridTemplateColumns: gridTemplate, alignItems: "stretch", borderBottom: "1px solid #f4f4f4" }}>
              {/* left title cell */}
              <div className={`grid-left-cell`} style={{ padding: "8px 12px", boxSizing: "border-box", display: "flex", flexDirection: "column", justifyContent: "center", cursor: "pointer", background: isSelected ? "rgba(25,118,210,0.04)" : "transparent" }} onClick={() => onTaskClick && onTaskClick(t)} title={t.description || ""}>
                <div className="left-title" style={{ fontWeight: 700, whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>{t.title}</div>
                <div className="left-date" style={{ fontSize: 12, opacity: 0.9, marginTop: 4 }}>
                  {range.unit === "hour" ? format(start, "MM/dd HH:mm", { locale: ja }) : format(start, "MM/dd", { locale: ja })}
                </div>
              </div>

              {/* empty cells */}
              {range.columns.map((_, ci) => (
                <div key={ci} className="grid-cell" style={{ minWidth: colWidth, height: 56, boxSizing: "border-box", borderLeft: "1px solid #f0f0f0" }} />
              ))}

              {/* task bar placed by grid-column on top of cells */}
              <div
                className={`task-bar${isSelected ? " selected" : ""}`}
                style={{
                  gridColumn: `${2 + startIdx} / ${2 + startIdx + span}`,
                  gridRow: 1,
                  background: t.color,
                  borderRadius: 6,
                  margin: 6,
                  height: 44,
                  alignSelf: "center",
                  zIndex: 2,
                  cursor: "pointer",
                }}
                onClick={() => onTaskClick && onTaskClick(t)}
                title={`${t.title}\n${t.description || ""}\n${format(start, "yyyy-MM-dd HH:mm", { locale: ja })} - ${format(end, "yyyy-MM-dd HH:mm", { locale: ja })}`}
              />
            </div>
          );
        })}
      </div>
    </div>
  );
}
